const div=document.getElementById("init-calc"),g=document.getElementById("growth-calc"),m=document.getElementById("multi-calc"),calc=document.getElementById("calc-val"),spec=document.getElementById("calc-val-spec");function runCalc(){getCalc(div.value,g.value,m.value)}const getCalc=(e,t,c)=>{axios.get("/calc?div="+e+"&g="+t+"&m="+c).then((e=>{calc.innerHTML=e.data?.state,spec.innerHTML="Multiplier = "+c+", Initial Crash Rate = "+(1/e.data?.div).toFixed(2)+"%, Growth Rate = "+e.data?.g+", CDF: P(X <= "+c+") = "+e.data?.cdf})).catch((e=>{console.log(e)}))};